import React from 'react';

interface Character {
    id: number;
    name: string;
    image: string;
    status: string;
}

const CharacterCard = ({
    id,
    name,
    image,
    status
}: Character) => {
    return (
        <article key={id} className="character">
            <p>{name}</p>
            <img alt={name} src={image}/>
            <p>
                <span className={status.toLowerCase()}></span>
                {status}
            </p>
        </article>
    );
};

export default CharacterCard;
