import React, {useEffect, useState} from 'react';
import {API_URL, ENDPOINTS} from '../../../global/Constants';
import {TopListEpisode} from '../../../global/Interfaces';
import EpisodesListCharacter from "./EpisodesListCharacter";
import axios from 'axios';
import '../../../assets/styles/components/EpisodesList.css';

const EpisodesList = () => {
    // Get all IDs based on fixed number that is known and ascending, ideally must be more dynamic.
    const allLocationIDs = Array.from({length: 126}, (_, index: number) => index + 1)
    const [allData, setAllData] = useState<[]>([]);

    const fetchAllData = async (): Promise<void> => {
        try {
            const response = await axios.get(`${API_URL}${ENDPOINTS.location}/${allLocationIDs.join(',')}`);
            setLocations(response.data);
        } catch (error) {
            console.error('Error', error);
        }

        try {
            // Fetch data from API 1
            const response1 = await fetch('https://api.example.com/api1');
            const api1Data = await response1.json();

            // Fetch data from API 2
            const response2 = await fetch('https://api.example.com/api2');
            const api2Data = await response2.json();

            // Fetch data from API 3
            const response3 = await fetch('https://api.example.com/api3');
            const api3Data = await response3.json();

            // Set the data in the state
            setData({
                api1Data,
                api2Data,
                api3Data,
            });
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    useEffect(() => {
        void fetchAllData();
    }, []);

    return (
        <div>
            <h1>Top 10 Episodes with Highest Count of Unique Characters' Origin Dimensions</h1>
            {/*{getTopEpisodes()}*/}
        </div>
    );
};

export default EpisodesList;
