import React, {useEffect, useState} from 'react';
import {API_URL, ENDPOINTS} from '../../../global/Constants';
import {TopListEpisode} from '../../../global/Interfaces';
import EpisodesListCharacter from "./EpisodesListCharacter";
import axios from 'axios';
import '../../../assets/styles/components/EpisodesList.css';

interface Data {
    api1Data: any; // Replace 'any' with the actual type of data from API 1
    api2Data: any; // Replace 'any' with the actual type of data from API 2
    api3Data: any; // Replace 'any' with the actual type of data from API 3
}

const EpisodesList = () => {
    // Get all IDs based on fixed number that is known and ascending, ideally must be more dynamic.
    const allLocationIDs = Array.from({length: 126}, (_, index: number) => index + 1);
    const [allData, setAllData] = useState<Data | null>(null);

    const fetchAllData = async (): Promise<void> => {
        try {
            // Fetch data from API 1
            const response1= await axios.get(`${API_URL}${ENDPOINTS.locations}/${allLocationIDs.join(',')}`);
            const api1Data = response1.data;

            // Fetch data from API 2
            const response2= await axios.get(`${API_URL}${ENDPOINTS.characters}/${allLocationIDs.join(',')}`);
            const api2Data = response2.data;

            // Fetch data from API 3
            const response3= await axios.get(`${API_URL}${ENDPOINTS.location}/${allLocationIDs.join(',')}`);
            const api3Data = response3.data;

            // Set the data in the state
            setAllData({
                api1Data,
                api2Data,
                api3Data,
            });
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    useEffect(() => {
        void fetchAllData();
    }, []);

    return (
        <div>
            <h1>Top 10 Episodes with Highest Count of Unique Characters' Origin Dimensions</h1>
            {/*{getTopEpisodes()}*/}
        </div>
    );
};

export default EpisodesList;
