import React, {useEffect, useState} from 'react';
import '../../../assets/styles/components/CharacterCard.css'
import axios from "axios";
import {API_URL, DEFAULT_CHARACTER, ENDPOINTS} from "../../../global/Constants";

interface Character {
    id: number;
    name: string;
    status: string;
    species: string;
    type: string;
    gender: string;
    origin: {
        name: string;
        url: string;
    },
    location: {
        name: string;
        url: string;
    },
    image: string;
    episode: Array<string>;
    url: string;
}

interface CharacterExtended {
    id: number;
    showExtendedCard: boolean;
}

const CharacterCardExtended = ({
    id,
    showExtendedCard
}: CharacterExtended) => {
    const [characterData, setCharacterData] = useState<Character>(DEFAULT_CHARACTER);

    useEffect((): void => {
        const fetchData = async (): Promise<void> => {
            try {
                const response = await axios.get(`${API_URL}${ENDPOINTS.characters}/${id}`);
                setCharacterData(response.data);
            } catch (error) {
            }
        };

        fetchData();
    }, [showExtendedCard]);

    return (
        <div className="character-extended">
            <p>{characterData.name}</p>
        </div>
    );
};

export default CharacterCardExtended;
