import { useState, useEffect } from 'react';
import { API_URL, ENDPOINTS } from '../../../global/Constants';
import axios from 'axios';
import CharacterCard from "../CharacterCard/CharaterCard";
import '../../../assets/styles/components/CharacterList.css';

interface Character {
    id: number;
    name: string;
    image: string;
    status: string;
}

const CharacterList = () => {
    let filterTimeout;

    const [filterName, setFilterName] = useState('');
    const [characters, setCharacters] = useState<Character[]>([]);
    const [loading, setLoading] = useState(true);

    const searchHandler = (query: string) => {
        clearTimeout(filterTimeout)
        if (!query) return setFilteredCities([])

        filterTimeout = setTimeout(() => {
            console.log('====>', query)
            setFilteredCities(citiesArray.filter(
                city => city.toLowerCase().includes(query.toLowerCase())
            ))
        }, 500)
    };

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(`${API_URL}${ENDPOINTS.characters}?name=${filterName}`);
                setCharacters(response.data.results);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching data:', error);
                setLoading(false);
            }
        };

        fetchData();
    }, [filterName]);

    return (
        <div className="characters-list-container">
            <div className="characters-list__search-wrapper">
                <input
                    className="characters-list__search-input"
                    type="search"
                    onChange={(event) => searchHandler(event.target.value)}
                />
            </div>
            <div className="characters-list">
                {loading ? (
                    <p>Loading...</p>
                ) : (
                    <>
                        {characters.map((character) => (
                            <CharacterCard
                                id={character.id}
                                name={character.name}
                                image={character.image}
                                status={character.status}
                            />
                        ))}
                    </>
                )}
            </div>
        </div>
    );
};

export default CharacterList;
