import React, { useState, useEffect } from 'react';
import { API_URL, ENDPOINTS } from '../../../global/Constants';
import { ListEpisode } from '../../../global/Interfaces';
import EpisodesListCharacter from './EpisodesListCharacter';
import axios from 'axios';
import '../../../assets/styles/components/EpisodesList.css';

const EpisodesList = () => {
    // Get all IDs based on fixed number that is known and ascending, ideally must be more dynamic.
    const [episodes, setEpisodes] = useState<ListEpisode[]>([]);

    console.log(`${allEpisodeIDs.join(',')}`);

    const getAllEpisodesNumber = async (): Promise<void> => {
        try {
            const response  = await axios.get(`${API_URL}${ENDPOINTS.episodes}`);
            return response.data.info.count;
        } catch (error) {
            console.error('Error', error);
        }
    };

    const fetchEpisodesData = async (): Promise<void> => {
        getAllEpisodesNumber()
        const allEpisodeIDs: Array<number> = Array.from({length: 51}, (_, index: number) => index + 1);

        try {
            const response = await axios.get(`${API_URL}${ENDPOINTS.episodes}/${allEpisodeIDs.join(',')}`);
            setEpisodes(response.data);
        } catch (error) {
            console.error('Error', error);
        }
    };

    useEffect(() => {
        void fetchEpisodesData();
    }, []);

    const getTopEpisodes = () => {
        const episodeMap: Record<string, Set<string>> = {};

        episodes.forEach((episode) => {
            episode.characters.forEach((characterUrl: string) => {
                // Get ID of episode from character url
                const episodeId = characterUrl.split('/character/')[1];

                if (!episodeMap[episodeId]) {
                    episodeMap[episodeId] = new Set();
                }

                episodeMap[episodeId].add(characterUrl);
            });
        });

        const sortedEpisodes = Object.keys(episodeMap).sort(
            (a, b) => episodeMap[b].size - episodeMap[a].size
        );

        return sortedEpisodes.slice(0, 10).map((episodeId: string) => {
            const episode = episodes.find((episode): boolean => episode.id.toString() === episodeId);
            const uniqueDimensions = Array.from(episodeMap[episodeId]);

            return (
                <div key={episodeId}>
                    <h2>{episode?.name}</h2>
                    <ul>
                        {uniqueDimensions.map((characterUrl: string) => {
                            return (
                                <li key={characterUrl}>
                                    <EpisodesListCharacter characterUrl={characterUrl} />
                                </li>
                            );
                        })}
                    </ul>
                </div>
            );
        });
    };

    return (
        <div>
            <h1>Top 10 Episodes with Highest Count of Unique Characters' Origin Dimensions</h1>
            {getTopEpisodes()}
        </div>
    );
};

export default EpisodesList;
