import React, { useState, useEffect } from 'react';
import { API_URL, ENDPOINTS } from '../../../global/Constants';
import {} from '../../../global/Interfaces';
import axios from 'axios';
import '../../../assets/styles/components/EpisodesList.css';

const EpisodesList = () => {
    const [filterName, setFilterName] = useState('');
    const [characters, setCharacters] = useState<Character[]>([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(false);

    const fetchCharacterData = async (): Promise<void> => {
        try {
            setError(false);
            const response = await axios.get(`${API_URL}${ENDPOINTS.characters}?${paramName}=${filterName}`);
            setCharacters(response.data.results);
            setLoading(false);
        } catch (error) {
            setError(true);
            setLoading(false);
        }
    };

    useEffect((): void => {
        void fetchCharacterData();
    }, [filterName]);

    return (
        <div className="episodes-list-container">
            <h2>Top 10 Episodes</h2>
        </div>
    );
};

export default EpisodesList;
