import React from 'react';
import '../../../assets/styles/components/CharacterCard.css'

interface Character {
    id: number;
    name: string;
    image: string;
    status: string;
    loading: boolean;
}

const CharacterCard = ({
    id,
    name,
    image,
    status,
    loading
}: Character) => {
    return (
        <article key={id} className={`character${loading ? ' loading' : ''}`}>
            <div className="character__image-wrapper">
                <img alt={name} src={image}/>
            </div>
            <div className="character__content-wrapper">
                <h2>{name}</h2>
                <p className="character__status">
                    <span className={status.toLowerCase()}></span>
                    {status}
                </p>
            </div>
        </article>
    );
};

export default CharacterCard;
