import React, { useState, useEffect } from 'react';
import { API_URL, ENDPOINTS } from '../../../global/Constants';
import axios from 'axios';
import CharacterCard from "../CharacterCard/CharaterCard";
import '../../../assets/styles/components/CharacterList.css';
import Loader from "../../UI/Loader/Loader";

interface Character {
    id: number;
    name: string;
    image: string;
    status: string;
}

const CharacterList = () => {
    let filterTimeout: NodeJS.Timeout;

    const [filterName, setFilterName] = useState('');
    const [characters, setCharacters] = useState<Character[]>([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(false);

    const searchHandler = (query: string) => {
        clearTimeout(filterTimeout)
        if (!query) {
            return setFilterName('');
        }

        filterTimeout = setTimeout(() => {
            setFilterName(query.toLowerCase());
        }, 500);
    };

    useEffect(() => {
        const fetchData = async () => {
            try {
                setError(false);
                const response = await axios.get(`${API_URL}${ENDPOINTS.characters}?name=${filterName}`);
                setCharacters(response.data.results);
                setLoading(false);
            } catch (error) {
                setError(true);
                console.error('Error fetching data:', error);
                setLoading(false);
            }
        };

        setTimeout(() => {
            setLoading(true);
            fetchData();
        }, 2000)
    }, [filterName]);

    return (
        <div className="characters-list-container">
            <div className="characters-list__search-wrapper">
                <input
                    className="characters-list__search-input"
                    type="search"
                    onChange={(event: React.ChangeEvent<HTMLInputElement>) => searchHandler(event.target.value)}
                />
                {error ? (
                    <p>Geen resultaten, probeer het opnieuw</p>
                ): null}
            </div>
            {!error ? (
                <div className="characters-list">
                    {loading ? (
                        <Loader/>
                    ) : (
                        <>
                            {characters.map((character) => (
                                <CharacterCard
                                    id={character.id}
                                    name={character.name}
                                    image={character.image}
                                    status={character.status}
                                />
                            ))}
                        </>
                    )}
                </div>
            ) : null}
        </div>
    );
};

export default CharacterList;
